(function() {
  var CryptoJS, SHA1, blobTypedArray, defer, shaArrayBuffer;

  SHA1 = (CryptoJS = require("./lib/crypto")).SHA1;

  module.exports = function(data, fn) {
    if (data instanceof Blob) {
      return blobTypedArray(data, function(arrayBuffer) {
        return fn(shaArrayBuffer(arrayBuffer));
      });
    } else if (data instanceof ArrayBuffer) {
      return defer(function() {
        return fn(shaArrayBuffer(data));
      });
    } else {
      return defer(function() {
        return fn(SHA1(data).toString());
      });
    }
  };

  defer = function(fn) {
    return setTimeout(fn, 0);
  };

  shaArrayBuffer = function(arrayBuffer) {
    return SHA1(CryptoJS.lib.WordArray.create(arrayBuffer)).toString();
  };

  blobTypedArray = function(blob, fn) {
    var reader;
    reader = new FileReader();
    reader.onloadend = function() {
      return fn(reader.result);
    };
    return reader.readAsArrayBuffer(blob);
  };

}).call(this);

