(function() {
  var SHA1;

  SHA1 = require("../main");

  describe("SHA1", function() {
    it("should hash stuff", function(done) {
      return SHA1("", function(sha) {
        assert.equal(sha, "da39a3ee5e6b4b0d3255bfef95601890afd80709");
        return done();
      });
    });
    it("should hash blobs", function(done) {
      var blob;
      blob = new Blob;
      return SHA1(blob, function(sha) {
        assert.equal(sha, "da39a3ee5e6b4b0d3255bfef95601890afd80709");
        return done();
      });
    });
    return it("should hash array buffers", function(done) {
      var arrayBuffer;
      arrayBuffer = new ArrayBuffer;
      return SHA1(arrayBuffer, function(sha) {
        assert.equal(sha, "da39a3ee5e6b4b0d3255bfef95601890afd80709");
        return done();
      });
    });
  });

}).call(this);
